import React, { useState, useRef, useEffect } from "react";
import { useInterval } from "./useInterval";
import {
  CANVAS_SIZE,
  SNAKE_START,
  APPLE_START,
  SCALE,
  SPEED,
  DIRECTIONS
} from "./constants";

const App = () => {
  const canvasRef = useRef();
  const [snake, setSnake] = useState(SNAKE_START);
  const [apple, setApple] = useState(APPLE_START);
  const [dir, setDir] = useState([0, -1]);
  const [speed, setSpeed] = useState(null);
  const [gameOver, setGameOver] = useState(false);

  useInterval(() => gameLoop(), speed);

  const endGame = () => {
    setSpeed(null);
    setGameOver(true);
  };

  const moveSnake = ({ keyCode }) =>
    keyCode >= 37 && keyCode <= 40 && setDir(DIRECTIONS[keyCode]);

  const createApple = () =>
    apple.map((_a, i) => Math.floor(Math.random() * (CANVAS_SIZE[i] / SCALE)));

  const checkCollision = (piece, snk = snake) => {
    if (
      piece[0] * SCALE >= CANVAS_SIZE[0] ||
      piece[0] < 0 ||
      piece[1] * SCALE >= CANVAS_SIZE[1] ||
      piece[1] < 0
    )
      return true;

    for (const segment of snk) {
      if (piece[0] === segment[0] && piece[1] === segment[1]) return true;
    }
    return false;
  };

  const checkAppleCollision = newSnake => {
    if (newSnake[0][0] === apple[0] && newSnake[0][1] === apple[1]) {
      let newApple = createApple();
      while (checkCollision(newApple, newSnake)) {
        newApple = createApple();
      }
      setApple(newApple);
      return true;
    }
    return false;
  };

  const gameLoop = () => {
    const snakeCopy = JSON.parse(JSON.stringify(snake));
    const newSnakeHead = [snakeCopy[0][0] + dir[0], snakeCopy[0][1] + dir[1]];
    snakeCopy.unshift(newSnakeHead);
    if (checkCollision(newSnakeHead)) endGame();
    if (!checkAppleCollision(snakeCopy)) snakeCopy.pop();
    setSnake(snakeCopy);
  };

  const startGame = () => {
    setSnake(SNAKE_START);
    setApple(APPLE_START);
    setDir([0, -1]);
    setSpeed(SPEED);
    setGameOver(false);
  };

  useEffect(() => {
    const context = canvasRef.current.getContext("2d");
    context.setTransform(SCALE, 0, 0, SCALE, 0, 0);
    context.clearRect(0, 0, window.innerWidth, window.innerHeight);

    // Draw snake head
    const [headX, headY] = snake[0];
    const headImg = new Image();
    headImg.src = "./icons/gift.png";
    headImg.onload = () => {
      console.log("Head image loaded successfully");
      context.drawImage(headImg, headX, headY, 1, 1); // Adjust the width and height as needed
    };
    headImg.onerror = () => {
      console.log("Failed to load head image");
    };

    // Draw snake body
    const bodyImg = new Image();
    bodyImg.src = "./icons/gifbft.png";
    bodyImg.onload = () => {
      console.log("Head image loaded successfully");
      snake.slice(1).forEach(([x, y]) => context.drawImage(bodyImg, x, y, 1, 1)); // Adjust the width and height as needed
    };
    bodyImg.onerror = () => {
      console.log("Failed to load body image");
    };

    // Draw apple
    const [appleX, appleY] = apple;
    const appleImg = new Image();
    appleImg.src = "./icons/gift.png";
    appleImg.onload = () => {
      console.log("Head image loaded successfully");
      context.drawImage(appleImg, appleX, appleY, 1, 1); // Adjust the width and height as needed
    };
    appleImg.onerror = () => {
      console.log("Failed to load apple image");
    };
  }, [snake, apple, gameOver]);


  //   context.fillStyle = "pink";
  //   snake.forEach(([x, y]) => context.fillRect(x, y, 1, 1));
  //   context.fillStyle = "lightblue";
  //   context.fillRect(apple[0], apple[1], 1, 1);
  // }, [snake, apple, gameOver]);

  return (
    <div role="button" tabIndex="0" onKeyDown={e => moveSnake(e)}>
      <canvas
        style={{ border: "1px solid black" }}
        ref={canvasRef}
        width={`${CANVAS_SIZE[0]}px`}
        height={`${CANVAS_SIZE[1]}px`}
      />
      {gameOver && <div>GAME OVER!</div>}
      <button onClick={startGame}>Start Game</button>
    </div>
  );
};

export default App;
